ngBoxuppApp.factory('User',function($http,$location){
	return {
		login : function(userName, password){

			return $http({
		 				method: 'POST',
				 		url: '/boxupp/resources/user/login',
				 		data: angular.toJson({loginID : userName, password : password})
		 			}).then(function(result){
		 				return result.data;
		 			});
		},
		getProjects : function(userID){
			return $http({
		 				method: 'GET',
				 		url: '/boxupp/resources/user/getProjects/' + userID + '/',
		 			}).then(function(result){
		 				return result.data;
		 			});	
		},
		signup : function(newUserDetail){
			return $http({
		 				method: 'POST',
				 		url: '/boxupp/resources/user/signup/',
				 		data : angular.toJson(newUserDetail)
		 			}).then(function(result){
		 				return result.data;
		 			});	
		},
		signout : function(){
			$location.path('/');
		},
		checkIfExists : function(userMailID){
			return $http({
		 				method: 'GET',
				 		url: '/boxupp/resources/user/idCheck/' + userMailID,
				 		
		 			}).then(function(result){
		 				return result.data;
		 			});		
		}
	}
});

